mapper.select:
SELECT COUNT(*)
FROM ADMIN_ACCESS_IP_LIST
WHERE IP_ADDRESS = #{accessIP}

SELECT ADPR_ID, ADPR_NAME ,ADPR_AUTH,ADPR_LAST_ACCESS
FROM ADMIN_PROFILE
WHERE ADPR_ID =#{adminId}
AND ADPR_PASSWORD =#{adminPW}
AND ADPR_DELFLAG ='N'

SELECT USER_ACCOUNT_ID, USER_EMAIL,USER_NAME,USER_NICKNAME, USER_AUTH,USER_PHONE_NUMBER,USER_BIRTH,USER_JOIN_DATE, USER_LAST_ACCESS_DATE,USER_GENDER,USER_SITE, USER_DELFLAG,USJE_JEONGJI_SANGTAE,RN FROM(SELECT USER_ACCOUNT_ID, USER_EMAIL,USER_NAME,USER_NICKNAME, USER_AUTH,USER_PHONE_NUMBER,USER_BIRTH,USER_JOIN_DATE, USER_LAST_ACCESS_DATE,USER_GENDER,USER_SITE, USER_DELFLAG,USJE_JEONGJI_SANGTAE,ROW_NUMBER() OVER(ORDER BY USER_ACCOUNT_ID DESC) AS RN FROM(SELECT USER_ACCOUNT_ID, USER_EMAIL,USER_NAME,USER_NICKNAME, USER_AUTH,USER_PHONE_NUMBER,USER_BIRTH,USER_JOIN_DATE, USER_LAST_ACCESS_DATE,USER_GENDER,USER_SITE, USER_DELFLAG,USJE_JEONGJI_SANGTAE FROM( SELECT up.USER_ACCOUNT_ID AS USER_ACCOUNT_ID, up.USER_EMAIL AS USER_EMAIL, up.USER_NAME AS USER_NAME, up.USER_NICKNAME AS USER_NICKNAME, up.USER_AUTH AS USER_AUTH, up.USER_PHONE_NUMBER AS USER_PHONE_NUMBER, up.USER_BIRTH AS USER_BIRTH, up.USER_JOIN_DATE AS USER_JOIN_DATE, up.USER_LAST_ACCESS_DATE AS USER_LAST_ACCESS_DATE, up.USER_GENDER AS USER_GENDER, up.USER_SITE AS USER_SITE, up.USER_DELFLAG AS USER_DELFLAG, uj.USJE_JEONGJI_SANGTAE AS USJE_JEONGJI_SANGTAE FROM USER_JEONGJI uj RIGHT OUTER JOIN USER_PROFILE up ON up.USER_ACCOUNT_ID = uj.USJE_ACCOUNT_ID UNION SELECT nup.USER_ACCOUNT_ID AS USER_ACCOUNT_ID, nup.USER_EMAIL AS USER_EMAIL, nup.USER_NAME AS USER_NAME, nup.USER_NICKNAME AS USER_NICKNAME, nup.USER_AUTH AS USER_AUTH, nup.USER_PHONE_NUMBER AS USER_PHONE_NUMBER, nup.USER_BIRTH AS USER_BIRTH, nup.USER_JOIN_DATE AS USER_JOIN_DATE, nup.USER_LAST_ACCESS_DATE AS USER_LAST_ACCESS_DATE, nup.USER_GENDER AS USER_GENDER, nup.USER_SITE AS USER_SITE, nup.USER_DELFLAG AS USER_DELFLAG, uj.USJE_JEONGJI_SANGTAE AS USJE_JEONGJI_SANGTAE FROM USER_JEONGJI uj RIGHT OUTER JOIN NAVER_USER_PROFILE nup ON nup.USER_ACCOUNT_ID = uj.USJE_ACCOUNT_ID UNION SELECT kup.USER_ACCOUNT_ID AS USER_ACCOUNT_ID, kup.USER_EMAIL AS USER_EMAIL, kup.USER_NAME AS USER_NAME, kup.USER_NICKNAME AS USER_NICKNAME, kup.USER_AUTH AS USER_AUTH, kup.USER_PHONE_NUMBER AS USER_PHONE_NUMBER, kup.USER_BIRTH AS USER_BIRTH, kup.USER_JOIN_DATE AS USER_JOIN_DATE, kup.USER_LAST_ACCESS_DATE AS USER_LAST_ACCESS_DATE, kup.USER_GENDER AS USER_GENDER, kup.USER_SITE AS USER_SITE, kup.USER_DELFLAG AS USER_DELFLAG, uj.USJE_JEONGJI_SANGTAE AS USJE_JEONGJI_SANGTAE FROM USER_JEONGJI uj RIGHT OUTER JOIN KAKAO_USER_PROFILE kup ON kup.USER_ACCOUNT_ID = uj.USJE_ACCOUNT_ID UNION SELECT gup.USER_ACCOUNT_ID AS USER_ACCOUNT_ID, gup.USER_EMAIL AS USER_EMAIL, gup.USER_NAME AS USER_NAME, gup.USER_NICKNAME AS USER_NICKNAME, gup.USER_AUTH AS USER_AUTH, gup.USER_PHONE_NUMBER AS USER_PHONE_NUMBER, gup.USER_BIRTH AS USER_BIRTH, gup.USER_JOIN_DATE AS USER_JOIN_DATE, gup.USER_LAST_ACCESS_DATE AS USER_LAST_ACCESS_DATE, gup.USER_GENDER AS USER_GENDER, gup.USER_SITE AS USER_SITE, gup.USER_DELFLAG AS USER_DELFLAG, uj.USJE_JEONGJI_SANGTAE AS USJE_JEONGJI_SANGTAE FROM USER_JEONGJI uj RIGHT OUTER JOIN GOOGLE_USER_PROFILE gup ON gup.USER_ACCOUNT_ID = uj.USJE_ACCOUNT_ID) WHERE USER_AUTH IN ('S','I')
AND USER_AUTH = #{userAuth}
AND USER_SITE = #{userSite}
AND USER_DELFLAG = #{userDelflag}
AND USER_GENDER = #{userGender}
AND USJE_JEONGJI_SANGTAE = #{userJeongJiSangTae}
)) WHERE RN BETWEEN #{start} AND #{end}
